mapper.select:
SELECT
FROM sys_resource t WHERE t.id IN
#{item}

SELECT
FROM sys_resource t WHERE t.type = 'menu' AND t.parent_id IN ( SELECT sss.id FROM sys_resource sss WHERE sss.parent_id = 0 ) AND t.permission IN
#{item}

SELECT
FROM sys_resource t WHERE t.parent_id=#{parentId} AND t.permission IN
#{item}

mapper.insert:
INSERT INTO sys_resource ( NAME, type, url, parent_id,
permission, available ) VALUE ( #{name}, #{type}, #{url}, #{parentId},
#{permission}, #{available} );

mapper.update:
UPDATE sys_resource t
t.name =#{name}, t.type=#{type}, t.url=#{url}, t.parent_id =#{parentId}, t.parent_ids =#{parentIds}, t.permission =#{permission}, t.available =#{available},
WHERE t.id = #{id}
